/**
 * @desc AnimationFrame
 * @param {speedFlag|number} 0: 1: -1:
 * @param {speed|number} 
 * @param {countCallback|Function} 
 * @param {mainFun|Function} 
 * @param {changeSpeed:numeber} /-
*/
// var speedFlag = 1 // 
// var count = 0 // 
// var speed = 330 // / (fun)
// var timer = null // id
// var startTime = window.performance.now();

class AnimationFrame {
	constructor({speedFlag=0, speed = 500, changeSpeed = 50, countCallback = () => {}, mainFun = () => {} } = {}) {
		this.speedFlag = speedFlag;
		this.speed = speed;
		this.mainFun = mainFun;
		this.countCallback = countCallback;
		this.timer = null;
		this.startTime = window.performance.now();
		this.count = 0;
		this.changeSpeed = changeSpeed;
		this.isStop = false;
		this.render();
	}
	// 
	render(timestamp) {
		if (this.isStop) return;
    const elapsedTime = timestamp - this.startTime;
    if (elapsedTime > this.speed) {
        this.startTime = timestamp;
        this.animate();
				this.handleSpeed();
    }
    this.timer = window.requestAnimationFrame(this.render.bind(this));
	}
	// 
	animate() {
		this.countCallback(this.count);
		if (this.isStop) return;
		this.mainFun(this.count);
		this.count++
	}
	// 
	handleSpeed() {
		if (this.speedFlag === 1) {
			// 
			this.speed>this.changeSpeed?this.speed -= this.changeSpeed:this.speedFlag = 0;
		} else if (this.speedFlag === -1) {
			this.speed += this.changeSpeed;
		}
	}
	// 
	quicken() {
		this.speedFlag = 1
	}
	// 
	slow() {
		this.speedFlag = -1
	}
	// 
	equally() {
		this.speedFlag = 0
	}
	// 
	stop() {
		cancelAnimationFrame(this.timer);
		this.isStop = true;
	}
}
```

