  name: string,
  age: number,
}
class Person3 implements person3Interface1,person3interface2 {
  public id:number = 12
  public type:string = 'worker'
  public name:string = 'levi'
  public age:number = 28
}
// interface继承
interface person3interface3 extends person3interface2 {
  gender:string
}
